tables.table_schema:
CREATE TABLE community_demographics (
    community_id INT PRIMARY KEY,
    community_name VARCHAR(50),
    population_size INT,
    pollution_level FLOAT
);

CREATE TABLE awareness_campaigns (
    campaign_id INT PRIMARY KEY,
    campaign_name VARCHAR(50),
    target_community_id INT,
    campaign_theme VARCHAR(50)
);

CREATE TABLE community_demographics (
    community_id UUID PRIMARY KEY,
    community_name TEXT NOT NULL,
    population INT NOT NULL,
    pollution_level FLOAT
);

CREATE TABLE awareness_campaigns (
    campaign_id UUID PRIMARY KEY,
    campaign_name TEXT NOT NULL,
    start_date DATE,
    end_date DATE
);

CREATE TABLE campaign_results (
    result_id UUID PRIMARY KEY,
    campaign_id UUID REFERENCES awareness_campaigns(campaign_id),
    community_id UUID REFERENCES community_demographics(community_id),
    outreach_success BOOLEAN
);
