tables.table_schema:
CREATE TABLE plastic_pollution_data (
  id INT PRIMARY KEY,
  country VARCHAR(255),
  pollution_level INT,
  year INT
);

CREATE TABLE plastic_pollution_data (
   country VARCHAR(50),
   year INT,
   plastic_waste_kg FLOAT,
   population INT
);

CREATE TABLE plastic_pollution_analysis (
   report_id INT,
   data_source VARCHAR(100),
   analysis_type VARCHAR(50),
   trend_analysis TEXT
);

CREATE TABLE policy_recommendations (
   country VARCHAR(50),
   recommendation TEXT,
   importance_level INT
);

CREATE TABLE plastic_pollution_data (
   id INT PRIMARY KEY,
   location VARCHAR(255),
   pollution_level DECIMAL
);

CREATE TABLE pollution_trends (
   trend_id INT PRIMARY KEY,
   trend_name VARCHAR(255),
   trend_description TEXT
);

CREATE TABLE policy_making (
   policy_id INT PRIMARY KEY,
   policy_name VARCHAR(255),
   policy_description TEXT
);

CREATE TABLE environmental_conservation (
   conservation_id INT PRIMARY KEY,
   conservation_strategy VARCHAR(255),
   conservation_impact TEXT
);
