tables.table_schema:
CREATE TABLE industry_compliance (
  industry_id UUID PRIMARY KEY,
  industry_name VARCHAR(255),
  plastic_waste_managed DECIMAL,
  regulatory_status BOOLEAN
);

CREATE TABLE IndustryCompliance (
  industry_id SERIAL PRIMARY KEY,
  industry_name VARCHAR(100) NOT NULL,
  plastic_waste_limit DECIMAL NOT NULL,
  compliance_status BOOLEAN
);

CREATE TABLE RegulatoryBody (
  regulatory_id SERIAL PRIMARY KEY,
  regulatory_name VARCHAR(100) NOT NULL
);
