tables.table_schema:
CREATE TABLE WasteCollectionSites (
    site_id INT PRIMARY KEY,
    site_name VARCHAR(50) NOT NULL,
    site_location VARCHAR(100) NOT NULL
);

CREATE TABLE biodegradable_waste_collection_sites (
    site_id INT PRIMARY KEY,
    site_name VARCHAR(255),
    location VARCHAR(255),
    capacity DECIMAL
);

CREATE TABLE collection_schedules (
  schedule_id UUID PRIMARY KEY,
  site_id UUID REFERENCES collection_sites(site_id),
  day_of_week TEXT NOT NULL,
  collection_time TIME
);

CREATE TABLE collected_waste (
  collection_id UUID PRIMARY KEY,
  schedule_id UUID REFERENCES collection_schedules(schedule_id),
  waste_type TEXT NOT NULL,
  weight FLOAT NOT NULL
);

CREATE TABLE waste_collection_employees (
  employee_id UUID PRIMARY KEY,
  employee_name TEXT NOT NULL,
  employee_role TEXT,
  hire_date DATE
);

CREATE TABLE BiodegradableSites (
  site_id INT PRIMARY KEY,
  site_name VARCHAR(255),
  site_location VARCHAR(255),
  site_capacity INT
);

CREATE TABLE CollectionSchedules (
  schedule_id INT PRIMARY KEY,
  site_id INT REFERENCES BiodegradableSites(site_id),
  collection_date DATE,
  collection_time TIME
);

CREATE TABLE WasteTypes (
  waste_type_id INT PRIMARY KEY,
  waste_type_name VARCHAR(255),
  waste_description TEXT
);

CREATE TABLE SiteAssignments (
  assignment_id INT PRIMARY KEY,
  site_id INT REFERENCES BiodegradableSites(site_id),
  collector_id INT,
  assignment_date DATE
);
