tables.table_schema:
CREATE TABLE households (
    house_id INT PRIMARY KEY,
    waste_weight DECIMAL
);

CREATE TABLE businesses (
    business_id INT PRIMARY KEY,
    waste_weight DECIMAL
);

CREATE TABLE households (
  id INT PRIMARY KEY,
  name VARCHAR(50),
  address VARCHAR(100),
  waste_generated FLOAT
);

CREATE TABLE businesses (
  id INT PRIMARY KEY,
  name VARCHAR(50),
  industry VARCHAR(50),
  waste_generated FLOAT
);

CREATE TABLE waste_collection (
  id INT PRIMARY KEY,
  collection_date DATE,
  weight_collected FLOAT,
  source_id INT
);

CREATE TABLE waste_disposal (
  id INT PRIMARY KEY,
  disposal_date DATE,
  weight_disposed FLOAT,
  disposal_site VARCHAR(100)
);

CREATE TABLE environmental_impact (
  id INT PRIMARY KEY,
  impact_type VARCHAR(50),
  impact_rating INT,
  source_id INT
);
