tables.table_schema:
CREATE TABLE biodegradable_waste_composition (
    id INT PRIMARY KEY,
    waste_type VARCHAR(255) NOT NULL,
    waste_component VARCHAR(255) NOT NULL,
    percentage FLOAT NOT NULL
);

CREATE TABLE recycling_composting_processes (
    id INT PRIMARY KEY,
    process_name VARCHAR(255) NOT NULL,
    description TEXT
);

CREATE TABLE ecological_impact (
    id INT PRIMARY KEY,
    impact_area VARCHAR(255) NOT NULL,
    impact_description TEXT
);

CREATE TABLE biodegradable_waste (
    id INT PRIMARY KEY,
    waste_type VARCHAR(255),
    waste_weight DECIMAL(10,2),
    compostable BOOLEAN
);

CREATE TABLE recycling_statistics (
    id INT PRIMARY KEY,
    waste_type VARCHAR(255),
    recycle_rate DECIMAL(5,2),
    collection_date DATE
);

CREATE TABLE composting_facilities (
    id INT PRIMARY KEY,
    facility_name VARCHAR(255),
    location VARCHAR(255),
    capacity DECIMAL(10,2)
);

CREATE TABLE biodegradable_waste ( 
  waste_id INT PRIMARY KEY, 
  category VARCHAR(50), 
  weight DECIMAL 
);

CREATE TABLE recycling_centers ( 
  center_id INT PRIMARY KEY, 
  location VARCHAR(100), 
  capacity INT 
);

CREATE TABLE composting_facilities ( 
  facility_id INT PRIMARY KEY, 
  facility_name VARCHAR(100), 
  location VARCHAR(100) 
);
