tables.table_schema:
CREATE TABLE biodegradable_waste_carbon_footprint (
    method_id INT PRIMARY KEY,
    method_name VARCHAR(50),
    waste_type VARCHAR(50),
    carbon_emission FLOAT
);

CREATE TABLE biodegradable_waste_disposal_methods (
  method_id UUID PRIMARY KEY,
  method_name VARCHAR(100) NOT NULL,
  carbon_footprint FLOAT NOT NULL
);

CREATE TABLE waste_disposal_locations (
  location_id UUID PRIMARY KEY,
  location_name VARCHAR(100) NOT NULL,
  capacity INTEGER NOT NULL
);

CREATE TABLE Waste_Disposal_Methods (
  method_id SERIAL PRIMARY KEY,
  method_name VARCHAR(50) UNIQUE NOT NULL,
  description TEXT,
  carbon_emission DECIMAL
);

CREATE TABLE Carbon_Footprint_Report (
  report_id SERIAL PRIMARY KEY,
  record_id INT REFERENCES Waste_Records(record_id),
  carbon_emission DECIMAL,
  report_date DATE
);

CREATE TABLE WasteTypes ( 
  waste_id INT PRIMARY KEY, 
  waste_name TEXT NOT NULL, 
  waste_description TEXT, 
  carbon_intensity FLOAT NOT NULL 
);

CREATE TABLE WasteDisposalMethods ( 
  method_id INT PRIMARY KEY, 
  method_name TEXT NOT NULL, 
  method_description TEXT, 
  effectiveness_rating INT 
);

CREATE TABLE WasteDisposalOptions ( 
  option_id INT PRIMARY KEY, 
  option_name TEXT NOT NULL, 
  option_description TEXT, 
  method_id INT REFERENCES WasteDisposalMethods(method_id) 
);

CREATE TABLE biodegradable_waste_disposal_methods (
  method_id INT PRIMARY KEY,
  method_name VARCHAR(50) NOT NULL,
  carbon_emission DECIMAL(10,2) NOT NULL,
  efficiency_rating INT NOT NULL
);

CREATE TABLE waste_materials (
  material_id INT PRIMARY KEY,
  material_name VARCHAR(50) NOT NULL,
  biodegradable BOOLEAN NOT NULL
);

CREATE TABLE waste_disposal_sites (
  site_id INT PRIMARY KEY,
  site_name VARCHAR(50) NOT NULL,
  location VARCHAR(100) NOT NULL
);

CREATE TABLE method_material_mapping (
  map_id INT PRIMARY KEY,
  method_id INT NOT NULL,
  material_id INT NOT NULL
);

CREATE TABLE carbon_footprint_history (
  log_id INT PRIMARY KEY,
  method_id INT NOT NULL,
  emission_value DECIMAL(10,2) NOT NULL,
  log_date DATE NOT NULL
);
