tables.table_schema:
CREATE TABLE biodegradable_waste_compliance (
  id INT PRIMARY KEY,
  report_date DATE,
  total_waste_weight DECIMAL,
  compliance_status BOOLEAN
);

CREATE TABLE waste_sources (
  source_id INT PRIMARY KEY,
  source_name VARCHAR(255),
  waste_type VARCHAR(50),
  compliance_level BOOLEAN
);

CREATE TABLE BiodegradableWasteReports (
    report_id INT PRIMARY KEY,
    report_date DATE,
    compliance_level VARCHAR(50),
    compliance_score INT
);

CREATE TABLE ComplianceStandards (
    standard_id INT PRIMARY KEY,
    standard_name VARCHAR(100),
    description TEXT,
    organization VARCHAR(100)
);

CREATE TABLE biodegradable_waste_compliance (
  report_id INT PRIMARY KEY,
  company_name VARCHAR(255),
  compliance_status BOOLEAN,
  compliance_date DATE
);

CREATE TABLE waste_reports (
  report_id INT PRIMARY KEY,
  waste_type VARCHAR(50),
  waste_quantity FLOAT,
  collection_date DATE
);

CREATE TABLE regulations (
  regulation_id INT PRIMARY KEY,
  regulation_name VARCHAR(100),
  regulation_description TEXT
);

CREATE TABLE waste_disposal_sites (
  disposal_site_id INT PRIMARY KEY,
  site_name VARCHAR(100),
  location VARCHAR(255)
);
