tables.table_schema:
CREATE TABLE biodegradable_waste_bags (
    id SERIAL PRIMARY KEY,
    bag_type VARCHAR(50) NOT NULL,
    bag_size VARCHAR(20) NOT NULL,
    quantity INT NOT NULL
);

CREATE TABLE containers_inventory (
    id SERIAL PRIMARY KEY,
    container_type VARCHAR(50) NOT NULL,
    capacity INT NOT NULL,
    available_quantity INT NOT NULL
);

CREATE TABLE inventory (
  id INT PRIMARY KEY,
  name VARCHAR(255),
  quantity INT,
  is_biodegradable BOOLEAN
);

CREATE TABLE households (
  household_id INT PRIMARY KEY,
  address VARCHAR(255),
  household_name VARCHAR(100),
  bag_id INT
);

CREATE TABLE businesses (
  business_id INT PRIMARY KEY,
  name VARCHAR(255),
  contact_person VARCHAR(100),
  container_id INT
);

CREATE TABLE biodegradable_bags (
    bag_id INT PRIMARY KEY,
    bag_size VARCHAR(50),
    bag_material VARCHAR(50),
    is_compostable BOOLEAN
);

CREATE TABLE biodegradable_containers (
    container_id INT PRIMARY KEY,
    container_size VARCHAR(50),
    container_material VARCHAR(50),
    is_compostable BOOLEAN
);

CREATE TABLE distribution_centers (
    center_id INT PRIMARY KEY,
    center_name VARCHAR(100),
    center_location VARCHAR(100),
    is_active BOOLEAN
);

CREATE TABLE DistributionCenters (
  id INT PRIMARY KEY,
  name VARCHAR(255),
  location VARCHAR(255),
  phone_number VARCHAR(20)
);

CREATE TABLE HouseholdInventory (
  id INT PRIMARY KEY,
  household_id INT,
  bag_id INT,
  quantity INT
);

CREATE TABLE BusinessInventory (
  id INT PRIMARY KEY,
  business_id INT,
  container_id INT,
  capacity INT
);
