tables.table_schema:
CREATE TABLE BiodegradableWasteDisposal (
    user_id INT PRIMARY KEY,
    disposal_date DATE,
    disposed_weight DECIMAL,
    disposal_location VARCHAR
);

CREATE TABLE ImprovementAreas (
    area_id SERIAL PRIMARY KEY,
    area_name VARCHAR,
    improvement_measure TEXT,
    suggested_action VARCHAR
);

CREATE TABLE Collection_Areas (
    area_id UUID PRIMARY KEY,
    area_name VARCHAR,
    city VARCHAR,
    region VARCHAR
);

CREATE TABLE Improvement_Plans (
    plan_id UUID PRIMARY KEY,
    area_id UUID REFERENCES Collection_Areas(area_id),
    action_plan TEXT,
    implementation_date DATE
);

CREATE TABLE BiodegradableWasteDisposalPatterns (
    id SERIAL PRIMARY KEY,
    location VARCHAR(255) NOT NULL,
    disposal_date DATE NOT NULL,
    disposal_method VARCHAR(50) NOT NULL
);

CREATE TABLE WasteType (
    id SERIAL PRIMARY KEY,
    type_name VARCHAR(100) NOT NULL,
    description TEXT
);

CREATE TABLE DisposalRegion (
    id SERIAL PRIMARY KEY,
    region_name VARCHAR(100) NOT NULL,
    region_code VARCHAR(10) UNIQUE
);

CREATE TABLE ImprovementAreas (
    id SERIAL PRIMARY KEY,
    area_name VARCHAR(150) NOT NULL,
    improvement_strategy TEXT
);

CREATE TABLE BiodegradableWaste ( 
 id UUID PRIMARY KEY, 
 waste_type TEXT, 
 disposal_location TEXT, 
 disposal_date DATE 
 );

CREATE TABLE DisposalLocation ( 
 location_name TEXT PRIMARY KEY, 
 postcode TEXT, 
 city TEXT, 
 region TEXT 
 );

CREATE TABLE WasteCollector ( 
 collector_id UUID PRIMARY KEY, 
 name TEXT, 
 phone_number TEXT, 
 email TEXT 
 );

CREATE TABLE CollectionSchedule ( 
 schedule_id UUID PRIMARY KEY, 
 collection_day TEXT, 
 collection_time TIME, 
 location_name TEXT REFERENCES DisposalLocation(location_name) 
 );
