  measure_name VARCHAR(100) NOT NULL,
  measure_description TEXT,
  measure_type VARCHAR(50) NOT NULL
);

CREATE TABLE WasteProcessingFacilities (
  facility_id SERIAL PRIMARY KEY,
  facility_name VARCHAR(150) NOT NULL,
  location VARCHAR(100) NOT NULL,
  facility_type VARCHAR(50)
);

CREATE TABLE QualityControlMeasures (
    id INT PRIMARY KEY,
    measure_name TEXT,
    description TEXT
);

CREATE TABLE WasteProcessingFacilities (
    id INT PRIMARY KEY,
    facility_name TEXT,
    location TEXT,
    capacity INT
);

CREATE TABLE BiodegradableWaste (
    id INT PRIMARY KEY,
    waste_type TEXT,
    weight DECIMAL,
    facility_id INT
);

CREATE TABLE OperationsLogs (
    id INT PRIMARY KEY,
    operation_date DATE,
    facility_id INT,
    action TEXT
);

CREATE TABLE Employees (
    id INT PRIMARY KEY,
    name TEXT,
    role TEXT,
    facility_id INT
);
