tables.table_schema:
CREATE TABLE GeographicInformationSystemData (
  id INT PRIMARY KEY,
  location_name VARCHAR(255),
  latitude DECIMAL(9,6),
  longitude DECIMAL(9,6)
);

CREATE TABLE BiodegradableWasteData (
  id INT PRIMARY KEY,
  waste_type VARCHAR(50),
  weight DECIMAL(8,2),
  disposal_method VARCHAR(100)
);

CREATE TABLE SpatialAnalysisData (
  id INT PRIMARY KEY,
  analysis_type VARCHAR(50),
  result TEXT,
  recommendation TEXT
);
