tables.table_schema:
CREATE TABLE user_weight_measurements (
    user_id UUID,
    weight DECIMAL,
    measurement_date DATE,
    PRIMARY KEY (user_id, measurement_date)
);

CREATE TABLE user_weights (
    user_id UUID PRIMARY KEY,
    weight DECIMAL,
    measurement_date DATE
);

CREATE TABLE user_heights (
    user_id UUID PRIMARY KEY,
    height DECIMAL,
    measurement_date DATE
);

CREATE TABLE user_body_fat (
    user_id UUID PRIMARY KEY,
    body_fat_percentage DECIMAL,
    measurement_date DATE
);

CREATE TABLE WeightMeasurements (
    id INTEGER PRIMARY KEY,
    user_id INTEGER,
    weight DECIMAL,
    measurement_date DATE
);

CREATE TABLE Users (
    id INTEGER PRIMARY KEY,
    username VARCHAR(50),
    email VARCHAR(100),
    registration_date DATE
);

CREATE TABLE UserProfiles (
    id INTEGER PRIMARY KEY,
    user_id INTEGER,
    gender VARCHAR(10),
    age INTEGER
);

CREATE TABLE UserWeight ( 
  user_id INT PRIMARY KEY, 
  weight DECIMAL, 
  measurement_date DATE 
);

CREATE TABLE UserHeight ( 
  user_id INT PRIMARY KEY, 
  height DECIMAL, 
  measurement_date DATE 
);

CREATE TABLE UserBMI ( 
  user_id INT PRIMARY KEY, 
  bmi DECIMAL, 
  measurement_date DATE 
);

CREATE TABLE UserBodyFatPercentage ( 
  user_id INT PRIMARY KEY, 
  body_fat_percentage DECIMAL, 
  measurement_date DATE 
);

CREATE TABLE UserCaloriesIntake ( 
  user_id INT PRIMARY KEY, 
  calories INT, 
  intake_date DATE 
);
