tables.table_schema:
CREATE TABLE ShoppingLists ( 
	id INT PRIMARY KEY, 
	meal_name VARCHAR(255) NOT NULL, 
	dietary_restriction VARCHAR(100) 
);

CREATE TABLE ShoppingList (
  list_id UUID PRIMARY KEY,
  list_name VARCHAR(50) NOT NULL,
  list_date DATE NOT NULL,
  list_items JSONB
);

CREATE TABLE meal_plans (
  meal_id INT PRIMARY KEY,
  meal_name VARCHAR(255),
  meal_description TEXT
);

CREATE TABLE shopping_lists (
  list_id INT PRIMARY KEY,
  list_name VARCHAR(255),
  list_items JSONB
);

CREATE TABLE dietary_restrictions (
  restriction_id INT PRIMARY KEY,
  restriction_name VARCHAR(255),
  restriction_description TEXT
);

CREATE TABLE MealPlans (
  meal_id INT PRIMARY KEY,
  meal_name VARCHAR(255) NOT NULL,
  meal_description TEXT,
  meal_date DATE
);

CREATE TABLE Meals (
  meal_id INT PRIMARY KEY,
  meal_name VARCHAR(255) NOT NULL,
  meal_category VARCHAR(50),
  meal_type ENUM('Breakfast', 'Lunch', 'Dinner', 'Snack')
);

CREATE TABLE ShoppingLists (
  list_id INT PRIMARY KEY,
  list_name VARCHAR(255) NOT NULL,
  list_date DATE,
  list_complete BOOLEAN
);

CREATE TABLE Ingredients (
  ingredient_id INT PRIMARY KEY,
  ingredient_name VARCHAR(255) NOT NULL,
  ingredient_category VARCHAR(50),
  ingredient_quantity INT
);

CREATE TABLE Restrictions (
  restriction_id INT PRIMARY KEY,
  restriction_name VARCHAR(100) NOT NULL,
  restriction_type VARCHAR(50),
  restriction_description TEXT
);
