tables.table_schema:
CREATE TABLE HydrationMonitor (
    person_id INT PRIMARY KEY,
    hydration_level FLOAT,
    intake_frequency INT
);

CREATE TABLE Hydration_Levels (
    user_id INT PRIMARY KEY,
    hydration_level FLOAT,
    last_updated TIMESTAMP
);

CREATE TABLE Intake_Frequency(
    user_id INT PRIMARY KEY,
    intake_count INT,
    last_intake TIMESTAMP
);

CREATE TABLE hydration_levels (
  user_id INT PRIMARY KEY,
  hydration_level INT,
  hydration_date DATE
);

CREATE TABLE intake_frequency (
  user_id INT PRIMARY KEY,
  water_intake INT,
  daily_frequency INT
);

CREATE TABLE user_details (
  user_id INT PRIMARY KEY,
  username TEXT,
  age INT,
  weight INT
);

CREATE TABLE hydration_levels (
    user_id INT PRIMARY KEY,
    hydration_level INT,
    timestamp TIMESTAMP
);

CREATE TABLE hydration_intake (
    intake_id INT PRIMARY KEY,
    user_id INT,
    intake_amount INT,
    intake_time TIMESTAMP
);

CREATE TABLE hydration_goals (
    user_id INT PRIMARY KEY,
    daily_goal INT
);

CREATE TABLE hydration_history (
    history_id INT PRIMARY KEY,
    user_id INT,
    intake_amount INT,
    intake_time TIMESTAMP
);

CREATE TABLE hydration_levels (
    id INT PRIMARY KEY,
    user_id INT,
    hydration_level FLOAT,
    measurement_datetime TIMESTAMP
);

CREATE TABLE water_intake (
    id INT PRIMARY KEY,
    user_id INT,
    amount_ml FLOAT,
    intake_datetime TIMESTAMP
);

CREATE TABLE intake_logs (
    id INT PRIMARY KEY,
    user_id INT,
    amount_ml FLOAT,
    log_datetime TIMESTAMP
);

CREATE TABLE users (
    id INT PRIMARY KEY,
    username VARCHAR(50),
    email VARCHAR(100)
);
