tables.table_schema:
CREATE TABLE training_plans (
  plan_id UUID PRIMARY KEY,
  plan_name VARCHAR(100),
  start_date DATE,
  end_date DATE
);

CREATE TABLE users (
  user_id INT PRIMARY KEY,
  name VARCHAR(50),
  email VARCHAR(100),
  age INT
);

CREATE TABLE training_plans (
  plan_id UUID PRIMARY KEY,
  plan_name VARCHAR(255),
  start_date DATE,
  end_date DATE
);

CREATE TABLE Training_Plans (
	plan_id INT PRIMARY KEY,
	plan_name VARCHAR(255) NOT NULL,
	start_date DATE,
	end_date DATE
);

CREATE TABLE Training_Days (
	day_id INT PRIMARY KEY,
	plan_id INT REFERENCES Training_Plans(plan_id),
	day_date DATE
);

CREATE TABLE Distance_Training (
	distance_id INT PRIMARY KEY,
	day_id INT REFERENCES Training_Days(day_id),
	distance_in_km FLOAT
);

CREATE TABLE Pace_Training (
	pace_id INT PRIMARY KEY,
	day_id INT REFERENCES Training_Days(day_id),
	pace_per_km TIME
);

CREATE TABLE Workout_Duration (
	duration_id INT PRIMARY KEY,
	day_id INT REFERENCES Training_Days(day_id),
	duration_minutes INT
);
