tables.table_schema:
CREATE TABLE trainer (
    trainer_id INT PRIMARY KEY,
    trainer_name VARCHAR(50),
    age INT,
    gender VARCHAR(10)
);

CREATE TABLE heart_rate_metrics (
  session_id UUID PRIMARY KEY,
  timestamp TIMESTAMP,
  heart_rate INT
);

CREATE TABLE speed_metrics (
  session_id UUID PRIMARY KEY,
  timestamp TIMESTAMP,
  speed FLOAT
);

CREATE TABLE distance_metrics (
  session_id UUID PRIMARY KEY,
  timestamp TIMESTAMP,
  distance FLOAT
);

CREATE TABLE heart_rate_data (
    session_id INT PRIMARY KEY,
    heart_rate INT,
    timestamp TIMESTAMP
);

CREATE TABLE speed_data (
    session_id INT PRIMARY KEY,
    speed FLOAT,
    timestamp TIMESTAMP
);

CREATE TABLE distance_data (
    session_id INT PRIMARY KEY,
    distance FLOAT,
    timestamp TIMESTAMP
);

CREATE TABLE training_sessions (
    session_id INT PRIMARY KEY,
    trainer_name VARCHAR(50),
    session_date DATE,
    session_duration INT
);
