CREATE TABLE marathon_races (
    race_id INT PRIMARY KEY,
    athlete_name VARCHAR(50) NOT NULL,
    finishing_time TIME NOT NULL,
    ranking INT
);

CREATE TABLE marathon_results (
    participant_id INT PRIMARY KEY,
    participant_name VARCHAR(255),
    finishing_time TIME,
    ranking INT
);

CREATE TABLE marathon_races (race_id INT PRIMARY KEY,
    race_name VARCHAR(50) NOT NULL,
    race_date DATE NOT NULL,
    race_distance DECIMAL(5,2) NOT NULL,
    race_location VARCHAR(100) NOT NULL
);

CREATE TABLE athletes (athlete_id INT PRIMARY KEY,
    athlete_name VARCHAR(50) NOT NULL,
    athlete_country VARCHAR(50) NOT NULL,
    athlete_gender CHAR(1) NOT NULL,
    athlete_birthdate DATE NOT NULL
);

CREATE TABLE race_results (result_id INT PRIMARY KEY,
    race_id INT REFERENCES marathon_races(race_id),
    athlete_id INT REFERENCES athletes(athlete_id),
    finishing_time TIME NOT NULL,
    ranking INT NOT NULL
);

CREATE TABLE marathon_results (
    athlete_id INT,
    race_date DATE,
    finishing_time TIME,
    ranking INT
);

CREATE TABLE athletes (
    athlete_id INT PRIMARY KEY,
    athlete_name VARCHAR(255),
    country VARCHAR(50)
);

CREATE TABLE races (
    race_id INT PRIMARY KEY,
    race_name VARCHAR(100),
    race_location VARCHAR(100),
    race_date DATE
);

CREATE TABLE countries (
    country_id INT PRIMARY KEY,
    country_name VARCHAR(50)
);
