tables.table_schema:
CREATE TABLE MealPlan (
	plan_id INT,
	meal_name VARCHAR,
	meal_time TIME,
	food_items JSON
);

CREATE TABLE NutritionInformation (
  date DATE,
  meal VARCHAR(255),
  food_item VARCHAR(255),
  calories INT
);

CREATE TABLE MealPlans (
  plan_id INT,
  plan_name VARCHAR(255),
  description TEXT
);

CREATE TABLE DietaryPreferences (
  user_id INT,
  preference_name VARCHAR(255),
  preference_value VARCHAR(255)
);

CREATE TABLE DietaryPreferences (
    id INT PRIMARY KEY,
    user_id INT,
    preference_name VARCHAR(255),
    FOREIGN KEY (user_id) REFERENCES User(id)
);
