tables.table_schema:
CREATE TABLE Athlete (
    id INT PRIMARY KEY,
    name VARCHAR,
    age INT,
    team VARCHAR
);

CREATE TABLE athlete_sleep_patterns (
  athlete_id INT PRIMARY KEY,
  date DATE,
  hours_slept DECIMAL,
  sleep_quality INT
);

CREATE TABLE athlete_activities (
  activity_id INT PRIMARY KEY,
  activity_name VARCHAR(50),
  activity_type VARCHAR(50),
  calories_burned INT
);

CREATE TABLE coach_feedback (
  feedback_id INT PRIMARY KEY,
  athlete_id INT,
  date DATE,
  feedback_text TEXT
);
