tables.table_schema:
CREATE TABLE athlete (
  athlete_id INT PRIMARY KEY,
  name VARCHAR(50),
  age INT,
  gender VARCHAR(10)
);

CREATE TABLE Athletes (
    athlete_id INT PRIMARY KEY,
    athlete_name VARCHAR(255) NOT NULL,
    athlete_gender VARCHAR(10) NOT NULL
);

CREATE TABLE athletes (
    athlete_id INT PRIMARY KEY,
    athlete_name VARCHAR(50),
    age INT,
    gender CHAR(1)
);

CREATE TABLE workouts (
    workout_id INT PRIMARY KEY,
    workout_name VARCHAR(50),
    workout_type VARCHAR(50),
    workout_intensity INT
);

CREATE TABLE leaderboards (
    leaderboard_id INT PRIMARY KEY,
    workout_id INT,
    position INT,
    athlete_id INT
);

CREATE TABLE ranks (
    rank_id INT PRIMARY KEY,
    athlete_id INT,
    total_points INT
);
