tables.table_schema:
CREATE TABLE athlete_progress_reports (
  athlete_id INT,
  progress_report_date TIMESTAMP,
  progress_description TEXT,
  PRIMARY KEY (athlete_id, progress_report_date)
);

CREATE TABLE athletes (
	athlete_id INT PRIMARY KEY,
	athlete_name VARCHAR(255),
	athlete_sport VARCHAR(255)
);

CREATE TABLE AthleteProgress (
    athlete_id INT,
    date DATE,
    progress_measure FLOAT
);

CREATE TABLE Attendance (
    athlete_id INT,
    date DATE,
    attendance_status VARCHAR(20)
);

CREATE TABLE PerformanceTrends (
    athlete_id INT,
    date DATE,
    performance_metric FLOAT
);

CREATE TABLE Athletes (
  athlete_id INT PRIMARY KEY,
  athlete_name VARCHAR(100) NOT NULL,
  athlete_team VARCHAR(50),
  athlete_age INT
);

CREATE TABLE Attendance (
  attendance_id INT PRIMARY KEY,
  athlete_id INT REFERENCES Athletes(athlete_id),
  attendance_date DATE,
  attendance_status VARCHAR(10) NOT NULL
);
