tables.table_schema:
CREATE TABLE reminders (
    reminder_id INT PRIMARY KEY,
    athlete_id INT,
    event_name VARCHAR(50),
    event_date DATE
);

CREATE TABLE competitions (
    competition_id INT PRIMARY KEY,
    athlete_id INT,
    competition_name VARCHAR(50),
    competition_date DATE
);

CREATE TABLE Events (
    event_id UUID PRIMARY KEY,
    event_name VARCHAR(255),
    event_date DATE,
    event_location VARCHAR(255)
);

CREATE TABLE Competitions (
    competition_id UUID PRIMARY KEY,
    competition_name VARCHAR(255),
    competition_date DATE,
    competition_location VARCHAR(255)
);

CREATE TABLE Athletes (
    athlete_id UUID PRIMARY KEY,
    athlete_name VARCHAR(255),
    athlete_sport VARCHAR(255),
    athlete_country VARCHAR(255)
);

CREATE TABLE Reminders (
    reminder_id UUID PRIMARY KEY,
    reminder_type VARCHAR(255),
    reminder_date DATE,
    athlete_id UUID REFERENCES Athletes(athlete_id)
);

CREATE TABLE reminders (
    reminder_id INT PRIMARY KEY,
    event_name VARCHAR(255),
    event_date DATE,
    event_description TEXT
);

CREATE TABLE competitions (
    competition_id INT PRIMARY KEY,
    competition_name VARCHAR(255),
    competition_date DATE,
    competition_location VARCHAR(255)
);

CREATE TABLE athletes (
    athlete_id INT PRIMARY KEY,
    athlete_name VARCHAR(255),
    athlete_sport VARCHAR(100),
    athlete_country VARCHAR(100)
);

CREATE TABLE promotions (
    promotion_id INT PRIMARY KEY,
    promotion_name VARCHAR(255),
    promotion_start_date DATE,
    promotion_end_date DATE
);
