tables.table_schema:
CREATE TABLE WorkoutPrograms (
  athlete_id UUID,
  program_name VARCHAR,
  goal VARCHAR,
  fitness_level VARCHAR
);

CREATE TABLE Athletes (
  athlete_id UUID PRIMARY KEY,
  name STRING,
  age INT,
  fitness_level STRING
);

CREATE TABLE Goals (
    goal_id INT PRIMARY KEY,
    athlete_id INT,
    goal_description VARCHAR(100),
    target_date DATE
);

CREATE TABLE FitnessLevels (
    level_id INT PRIMARY KEY,
    athlete_id INT,
    level_description VARCHAR(100),
    initial_fitness_score INT
);

CREATE TABLE WorkoutPrograms (
    program_id INT PRIMARY KEY,
    athlete_id INT,
    program_name VARCHAR(50),
    program_description VARCHAR(100)
);
