tables.table_schema:
CREATE TABLE weightlifting_performance (
  athlete_id INT,
  date DATE,
  weightlifting_category VARCHAR,
  lift_weight DECIMAL
);

CREATE TABLE weightlifting_events (
  event_id INT,
  event_name VARCHAR,
  event_date DATE,
  location VARCHAR
);

CREATE TABLE weightlifting_athletes (
    athlete_id INT PRIMARY KEY,
    athlete_name VARCHAR(50) NOT NULL,
    athlete_country VARCHAR(50) NOT NULL
);

CREATE TABLE weightlifting_competitions (
    competition_id INT PRIMARY KEY,
    competition_name VARCHAR(50) NOT NULL,
    competition_date DATE NOT NULL,
    location VARCHAR(50) NOT NULL
);
