tables.table_schema:
CREATE TABLE therapy_sessions (
  session_id INT PRIMARY KEY,
  therapist_name TEXT,
  session_date DATE,
  session_time TIME
);

CREATE TABLE therapy_sessions (
  session_id INT PRIMARY KEY,
  therapist_name VARCHAR(50),
  client_name VARCHAR(50),
  appointment_time TIMESTAMP
);

CREATE TABLE support_groups (
  group_id INT PRIMARY KEY,
  group_name VARCHAR(50),
  meeting_time TIMESTAMP,
  facilitator_name VARCHAR(50)
);

CREATE TABLE support_groups (
    group_id INT PRIMARY KEY,
    group_name VARCHAR(255),
    group_description TEXT
);

CREATE TABLE reminders (
    reminder_id INT PRIMARY KEY,
    reminder_text TEXT,
    reminder_date DATE,
    reminder_time TIME
);

CREATE TABLE users (
    user_id INT PRIMARY KEY,
    username VARCHAR(255),
    email VARCHAR(255),
    phone_number VARCHAR(20)
);

CREATE TABLE therapist (
  therapist_id INT PRIMARY KEY,
  therapist_name VARCHAR(50) NOT NULL,
  therapist_email VARCHAR(100) UNIQUE
);

CREATE TABLE client (
  client_id INT PRIMARY KEY,
  client_name VARCHAR(50) NOT NULL,
  client_email VARCHAR(100) UNIQUE
);

CREATE TABLE support_group (
  group_id INT PRIMARY KEY,
  group_name VARCHAR(50) NOT NULL,
  group_location VARCHAR(100),
  group_schedule VARCHAR(50)
);
