tables.table_schema:
CREATE TABLE trails (
    id SERIAL PRIMARY KEY,
    name VARCHAR(255),
    difficulty VARCHAR(50),
    length DECIMAL
);

CREATE TABLE trail_reviews (
    id SERIAL PRIMARY KEY,
    trail_id INT,
    review_text TEXT,
    rating INT
);

CREATE TABLE trails (
    trail_id INT PRIMARY KEY,
    trail_name VARCHAR(50) NOT NULL,
    difficulty_level VARCHAR(20) NOT NULL,
    trail_length DECIMAL(5,2) NOT NULL
);

CREATE TABLE trail_activities (
    activity_id INT PRIMARY KEY,
    activity_name VARCHAR(50) NOT NULL
);

CREATE TABLE trail_photos (
    photo_id INT PRIMARY KEY,
    trail_id INT NOT NULL,
    photo_url VARCHAR(255) NOT NULL,
    FOREIGN KEY (trail_id) REFERENCES trails(trail_id)
);
