tables.table_schema:
CREATE TABLE instructor (
    instructor_id SERIAL PRIMARY KEY,
    instructor_name VARCHAR(50) NOT NULL,
    instructor_email VARCHAR(50) NOT NULL,
    instructor_phone VARCHAR(15) NOT NULL
);

CREATE TABLE class_instructor (
    class_id INT,
    instructor_id INT,
    FOREIGN KEY (class_id) REFERENCES class_schedule(class_id),
    FOREIGN KEY (instructor_id) REFERENCES instructor_availability(instructor_id)
);

CREATE TABLE instructor_rating (
    instructor_id INT PRIMARY KEY,
    rating FLOAT,
    total_ratings INT
);

CREATE TABLE class_schedule (
    schedule_id INT PRIMARY KEY,
    class_name VARCHAR(255),
    instructor_id INT,
    day_of_week VARCHAR(20),
    time_start TIME,
    time_end TIME
);

CREATE TABLE instructor (
    instructor_id INT PRIMARY KEY,
    instructor_name VARCHAR(255),
    email VARCHAR(255),
    phone_number VARCHAR(20)
);

CREATE TABLE class_location (
    location_id INT PRIMARY KEY,
    location_name VARCHAR(255),
    address VARCHAR(255),
    capacity INT
);

CREATE TABLE class_enrollment (
    enrollment_id INT PRIMARY KEY,
    schedule_id INT,
    student_id INT
);

CREATE TABLE instructor_availability (
    availability_id INT PRIMARY KEY,
    instructor_id INT,
    day_of_week VARCHAR(20),
    time_start TIME,
    time_end TIME
);
