tables.table_schema:
CREATE TABLE client_progress (
    client_id UUID PRIMARY KEY,
    progress TEXT,
    goals TEXT,
    recommendations TEXT
);

CREATE TABLE clients_progress (
    client_id INT PRIMARY KEY,
    client_name VARCHAR(255),
    progress_status VARCHAR(50),
    goal_description TEXT
);

CREATE TABLE clients (
    client_id INT PRIMARY KEY,
    name VARCHAR(50),
    email VARCHAR(100),
    age INT
);

CREATE TABLE recommendations (
    recommendation_id INT PRIMARY KEY,
    client_id INT REFERENCES clients(client_id),
    recommendation VARCHAR(255),
    relevance_score FLOAT
);

CREATE TABLE ClientProgress (
    client_id INT PRIMARY KEY,
    progress INT,
    goal TEXT
);

CREATE TABLE PersonalizedRecommendations (
    recommendation_id INT PRIMARY KEY,
    recommendation TEXT
);

CREATE TABLE ClientDetails (
    client_id INT PRIMARY KEY,
    name TEXT,
    age INT,
    email TEXT
);

CREATE TABLE ClientGoals (
    goal_id INT PRIMARY KEY,
    client_id INT,
    goal_description TEXT,
    deadline DATE
);

CREATE TABLE progress (
  progress_id INT PRIMARY KEY,
  goal_id INT REFERENCES goals(goal_id),
  status VARCHAR(20),
  updated_at TIMESTAMP
);

CREATE TABLE recommendations (
  recommendation_id INT PRIMARY KEY,
  goal_id INT REFERENCES goals(goal_id),
  content TEXT,
  created_at TIMESTAMP
);

CREATE TABLE categories (
  category_id INT PRIMARY KEY,
  name VARCHAR(50) UNIQUE
);
