tables.table_schema:
CREATE TABLE Athletes (
    athlete_id INT PRIMARY KEY,
    name VARCHAR,
    age INT,
    gender VARCHAR
);

CREATE TABLE RecoveryTimelines (
    timeline_id INT PRIMARY KEY,
    athlete_id INT,
    recovery_stage VARCHAR,
    recovery_date DATE
);

CREATE TABLE Physicians (
    physician_id INT PRIMARY KEY,
    name VARCHAR,
    specialty VARCHAR
);
