tables.table_schema:
CREATE TABLE fitness_classes (
    class_id INT PRIMARY KEY,
    class_name VARCHAR(50) NOT NULL,
    instructor_name VARCHAR(50) NOT NULL
);

CREATE TABLE class_attendance (
    attendance_id INT PRIMARY KEY,
    class_id INT REFERENCES fitness_classes(class_id),
    member_id INT NOT NULL,
    attendance_date DATE
);

CREATE TABLE FitnessClasses (
    class_id INT PRIMARY KEY,
    class_name VARCHAR(50) NOT NULL,
    instructor_name VARCHAR(50) NOT NULL
);

CREATE TABLE Attendees (
    attendee_id INT PRIMARY KEY,
    attendee_name VARCHAR(50) NOT NULL
);

CREATE TABLE GroupFitnessClasses (
    class_id INT PRIMARY KEY,
    class_name VARCHAR(50) NOT NULL,
    instructor_name VARCHAR(50) NOT NULL,
    class_date DATE NOT NULL
);

CREATE TABLE ClassAttendance (
    attendance_id INT PRIMARY KEY,
    class_id INT REFERENCES GroupFitnessClasses(class_id),
    member_id INT NOT NULL,
    attendance_date DATE NOT NULL
);

CREATE TABLE Members (
    member_id INT PRIMARY KEY,
    member_name VARCHAR(50) NOT NULL,
    member_email VARCHAR(50) UNIQUE
);

CREATE TABLE Instructors (
    instructor_id INT PRIMARY KEY,
    instructor_name VARCHAR(50) NOT NULL,
    instructor_email VARCHAR(50) UNIQUE
);

CREATE TABLE Instructors (
  instructor_id INT,
  instructor_name VARCHAR(50),
  expertise_area VARCHAR(50),
  contact_info VARCHAR(50)
);

CREATE TABLE ClassLocations (
  location_id INT,
  location_name VARCHAR(50),
  address VARCHAR(100),
  capacity INT
);

CREATE TABLE ClassCategories (
  category_id INT,
  category_name VARCHAR(50)
);
