tables.table_schema:
CREATE TABLE class_participation (
    participant_id INT,
    class_id INT,
    participation_date DATE
);

CREATE TABLE fitness_classes (
    class_id INT,
    class_name VARCHAR(255),
    instructor_name VARCHAR(255),
    class_schedule VARCHAR(50)
);

CREATE TABLE GroupFitnessClasses (
    class_id SERIAL PRIMARY KEY,
    class_name VARCHAR(50) NOT NULL,
    class_instructor VARCHAR(50) NOT NULL,
    class_schedule TIMESTAMPTZ NOT NULL
);

CREATE TABLE MemberData (
    member_id SERIAL PRIMARY KEY,
    member_name VARCHAR(50) NOT NULL,
    member_email VARCHAR(50) UNIQUE NOT NULL,
    member_join_date DATE NOT NULL
);

CREATE TABLE class_participation (
    member_id INT,
    class_id INT,
    participation_date DATE,
    participation_time TIME
);
