tables.table_schema:
CREATE TABLE athlete_nutrition_plan (
    athlete_id INT PRIMARY KEY,
    dietary_preferences VARCHAR(255),
    intake_habits TEXT
);

CREATE TABLE dietary_preferences(
    athlete_id INT,
    preference_name VARCHAR(255),
    preference_description TEXT
);

CREATE TABLE performance_optimization (
    id SERIAL PRIMARY KEY,
    athlete_id INTEGER,
    performance_metric VARCHAR(50),
    metric_value DECIMAL
);
