tables.table_schema:
CREATE TABLE artist_profiles (
    artist_id INT PRIMARY KEY,
    biography TEXT,
    portfolio TEXT,
    exhibition_history TEXT
);

CREATE TABLE artists (
    artist_id UUID PRIMARY KEY,
    name VARCHAR(255),
    bio TEXT
);

CREATE TABLE portfolios (
    portfolio_id UUID PRIMARY KEY,
    artist_id UUID REFERENCES artists(artist_id),
    title VARCHAR(255),
    description TEXT
);

CREATE TABLE exhibitions (
    exhibition_id UUID PRIMARY KEY,
    artist_id UUID REFERENCES artists(artist_id),
    exhibition_date DATE,
    location VARCHAR(255)
);

CREATE TABLE artworks (
    artwork_id UUID PRIMARY KEY,
    portfolio_id UUID REFERENCES portfolios(portfolio_id),
    title VARCHAR(255),
    year_created INTEGER
);

CREATE TABLE ArtistProfile (
    artist_id SERIAL PRIMARY KEY,
    artist_name VARCHAR(50) NOT NULL,
    biography TEXT,
    birth_date DATE
);

CREATE TABLE ArtworkTags (
    artwork_id INT REFERENCES Artwork(artwork_id),
    tag_name VARCHAR(50) NOT NULL
);
