tables.table_schema:
CREATE TABLE commissioned_paintings (
    painting_id INT PRIMARY KEY,
    status VARCHAR(50),
    deadline DATE,
    client_specifications TEXT
);

CREATE TABLE painting_status (
    id INT,
    status_name VARCHAR(50),
    description TEXT
);

CREATE TABLE clients (
    id INT,
    client_name VARCHAR(255),
    address VARCHAR(255),
    phone_number VARCHAR(20)
);
