tables.table_schema:
CREATE TABLE competition_participants (
    participant_id INT PRIMARY KEY,
    participant_name VARCHAR(255),
    painting_title VARCHAR(255),
    painting_theme VARCHAR(255)
);

CREATE TABLE competition_judges (
    judge_id INT PRIMARY KEY,
    judge_name VARCHAR(255),
    judging_criteria TEXT,
    judging_experience INT
);

CREATE TABLE judges (
    judge_id INT PRIMARY KEY,
    judge_name VARCHAR(100),
    judge_email VARCHAR(255),
    specialization VARCHAR(100)
);

CREATE TABLE judging_criteria (
    criteria_id INT PRIMARY KEY,
    criteria_description TEXT,
    weightage DECIMAL
);

CREATE TABLE winners (
    winner_id INT PRIMARY KEY,
    participant_id INT,
    painting_rank INT,
    prize_amount DECIMAL
);

CREATE TABLE Participants (
    participant_id INT PRIMARY KEY,
    participant_name VARCHAR(255),
    participant_email VARCHAR(255),
    registration_date TIMESTAMP
);

CREATE TABLE Judges (
    judge_id INT PRIMARY KEY,
    judge_name VARCHAR(255),
    judge_experience_years INT,
    judge_rating FLOAT
);

CREATE TABLE Criteria (
    criteria_id INT PRIMARY KEY,
    criteria_name VARCHAR(255),
    criteria_description TEXT,
    max_points INT
);
