tables.table_schema:
CREATE TABLE TourSchedule (
    tour_id UUID PRIMARY KEY,
    artist_name VARCHAR(255),
    venue_id UUID,
    tour_date DATE
);

CREATE TABLE VenueInformation (
    venue_id UUID PRIMARY KEY,
    venue_name VARCHAR(255),
    location VARCHAR(255),
    capacity INT
);

CREATE TABLE venues (
    venue_id INT PRIMARY KEY,
    venue_name VARCHAR(255),
    location VARCHAR(255),
    capacity INT
);

CREATE TABLE artists (
    artist_id INT PRIMARY KEY,
    artist_name VARCHAR(255),
    genre VARCHAR(50)
);

CREATE TABLE artist (
    artist_id INT PRIMARY KEY,
    artist_name VARCHAR(255) NOT NULL,
    genre VARCHAR(255),
    booking_agent VARCHAR(255)
);

CREATE TABLE venues (
    venue_id UUID PRIMARY KEY,
    venue_name STRING,
    venue_city STRING,
    venue_capacity INT
);

CREATE TABLE artists (
    artist_id UUID PRIMARY KEY,
