CREATE TABLE Artists (
    artist_id SERIAL PRIMARY KEY,
    artist_name VARCHAR(255) NOT NULL,
    birth_date DATE,
    nationality VARCHAR(100)
);

CREATE TABLE Sculptures (
    sculpture_id SERIAL PRIMARY KEY,
    sculpture_name VARCHAR(255) NOT NULL,
    creation_date DATE,
    material VARCHAR(100),
    artist_id INT REFERENCES Artists(artist_id)
);

CREATE TABLE ArtCollections (
    collection_id SERIAL PRIMARY KEY,
    collection_name VARCHAR(255) NOT NULL,
    year_established DATE
);

CREATE TABLE CollectionItems (
    item_id SERIAL PRIMARY KEY,
    item_name VARCHAR(255) NOT NULL,
    acquisition_date DATE,
    artist_id INT REFERENCES Artists(artist_id),
    sculpture_id INT REFERENCES Sculptures(sculpture_id),
    collection_id INT REFERENCES ArtCollections(collection_id)
);

CREATE TABLE Galleries (
    gallery_id SERIAL PRIMARY KEY,
    gallery_name VARCHAR(255) NOT NULL,
    location VARCHAR(255) NOT NULL
);
