tables.table_schema:
CREATE TABLE dance_studio_membership (
  member_id INT PRIMARY KEY,
  member_name VARCHAR(50),
  contact_details VARCHAR(100),
  membership_status VARCHAR(20)
);

CREATE TABLE dance_studio_members (
    member_id INT PRIMARY KEY,
    member_name VARCHAR(50),
    contact_details VARCHAR(100),
    membership_status VARCHAR(20)
);

CREATE TABLE membership_payments (
    payment_id INT PRIMARY KEY,
    member_id INT,
    payment_date DATE,
    amount DECIMAL(10, 2),
    FOREIGN KEY (member_id) REFERENCES dance_studio_members(member_id)
);
