tables.table_schema:
CREATE TABLE Performers (
    performer_id SERIAL PRIMARY KEY,
    performer_name VARCHAR(255),
    email VARCHAR(255),
    phone_number VARCHAR(20)
);

CREATE TABLE dance_dates (
    date_id INT PRIMARY KEY,
    performance_date DATE
);

CREATE TABLE dance_venues (
    venue_id INT PRIMARY KEY,
    venue_name VARCHAR(255),
    location VARCHAR(255)
);

CREATE TABLE dance_performers (
    performer_id INT PRIMARY KEY,
    performer_name VARCHAR(255),
    skill_level VARCHAR(50)
);

CREATE TABLE dance_performance_dates (
    date_id INT PRIMARY KEY,
    performance_date DATE
);

CREATE TABLE performance_venues (
    venue_id INT PRIMARY KEY,
    venue_name VARCHAR(50)
);

CREATE TABLE performers (
    performer_id INT PRIMARY KEY,
    performer_name VARCHAR(50),
    performer_type VARCHAR(50)
);

CREATE TABLE ticket_sales (
    transaction_id INT PRIMARY KEY,
    ticket_price DECIMAL(10,2),
    number_of_tickets INT,
    performer_id INT REFERENCES performers(performer_id)
);

CREATE TABLE Dates (
    date_id INT PRIMARY KEY,
    performance_date DATE
);

CREATE TABLE Venues (
    venue_id INT PRIMARY KEY,
    venue_name VARCHAR(50),
    venue_address VARCHAR(100)
);

CREATE TABLE Performers (
    performer_id INT PRIMARY KEY,
