tables.table_schema:
CREATE TABLE dancers (
  dancer_id SERIAL PRIMARY KEY,
  name TEXT NOT NULL,
  age INT,
  choreography_id INT REFERENCES choreography_details(choreography_id)
);

CREATE TABLE dance_routines (
    routine_id INT PRIMARY KEY,
    routine_name VARCHAR(255),
    choreographer VARCHAR(255),
    duration_minutes INT
);

CREATE TABLE Choreographers (
	choreographer_id SERIAL PRIMARY KEY,
	choreographer_name VARCHAR(255) NOT NULL,
	phone_number VARCHAR(15),
	email VARCHAR(255)
);

CREATE TABLE Dancers (
	dancer_id SERIAL PRIMARY KEY,
	dancer_name VARCHAR(255) NOT NULL,
	dancer_type VARCHAR(50)
);

CREATE TABLE Performances (
	performance_id SERIAL PRIMARY KEY,
	performance_date DATE,
	venue VARCHAR(255),
	dance_id INT REFERENCES DanceRoutines(dance_id)
);

CREATE TABLE DanceGroups (
	group_id SERIAL PRIMARY KEY,
	group_name VARCHAR(255) NOT NULL,
	group_type VARCHAR(50),
	choreographer_id INT REFERENCES Choreographers(choreographer_id)
);
