tables.table_schema:
CREATE TABLE performance_schedule (
    performance_id INT,
    performance_date DATE,
    performance_time TIME,
    venue VARCHAR(100)
);

CREATE TABLE costumes (
    id SERIAL PRIMARY KEY,
    style VARCHAR(50),
    size VARCHAR(10),
    availability BOOLEAN
);

CREATE TABLE dance_performances (
    id SERIAL PRIMARY KEY,
    performance_date DATE,
    location VARCHAR(100)
);

CREATE TABLE costume_inventory (
    id SERIAL PRIMARY KEY,
    costume_id INT REFERENCES costumes(id),
    quantity INT,
    location VARCHAR(100)
);

CREATE TABLE performance_costume_mapping (
    performance_id INT REFERENCES dance_performances(id),
    costume_id INT REFERENCES costumes(id),
    quantity_needed INT,
    PRIMARY KEY (performance_id, costume_id)
);
