tables.table_schema:
CREATE TABLE DanceGroup (
    group_id INT PRIMARY KEY,
    group_name VARCHAR(50),
    group_leader_name VARCHAR(50),
    group_leader_contact VARCHAR(20)
);

CREATE TABLE MedicalCondition ( 
    condition_id UUID PRIMARY KEY, 
    dancer_id UUID, 
    condition_name STRING, 
    description STRING 
);

CREATE TABLE Allergy ( 
    allergy_id UUID PRIMARY KEY, 
    dancer_id UUID, 
    allergy_name STRING, 
    severity STRING 
);

CREATE TABLE NextOfKin (
    NextOfKinID INT PRIMARY KEY,
    NextOfKinName VARCHAR(50) NOT NULL,
    RelationshipToDancer VARCHAR(50) NOT NULL,
    NextOfKinPhoneNumber VARCHAR(15) NOT NULL
);

CREATE TABLE MedicalConditions (
    MedicalConditionID INT PRIMARY KEY,
    DancerID INT REFERENCES DancerInfo(DancerID) ON DELETE CASCADE,
    ConditionDescription TEXT NOT NULL
);

CREATE TABLE Allergies (
    AllergyID INT PRIMARY KEY,
    DancerID INT REFERENCES DancerInfo(DancerID) ON DELETE CASCADE,
    AllergyDescription TEXT NOT NULL
);

CREATE TABLE EmergencyContact (
    ContactID INT PRIMARY KEY,
    DancerID INT REFERENCES DancerInfo(DancerID) ON DELETE CASCADE,
    ContactName VARCHAR(50) NOT NULL,
    ContactPhoneNumber VARCHAR(15) NOT NULL
);
