tables.table_schema:
CREATE TABLE image_metadata (
    image_id UUID PRIMARY KEY,
    file_name VARCHAR(255) NOT NULL,
    resolution VARCHAR(20) NOT NULL,
    keywords JSONB
);

CREATE TABLE image_metadata (
    file_name VARCHAR(255),
    resolution VARCHAR(50),
    keywords VARCHAR(255)
);

CREATE TABLE image_category (
    category_name VARCHAR(50),
    image_id INT, 
    FOREIGN KEY (image_id) REFERENCES image_metadata()
);

CREATE TABLE image_files (
    file_id SERIAL PRIMARY KEY,
    file_name VARCHAR(255) NOT NULL,
    resolution VARCHAR(10) NOT NULL,
    keywords TEXT
);

CREATE TABLE image_resolutions (
    resolution_id SERIAL PRIMARY KEY,
    resolution_name VARCHAR(20) NOT NULL
);

CREATE TABLE image_keywords (
    keyword_id SERIAL PRIMARY KEY,
    keyword_name VARCHAR(50) NOT NULL
);

CREATE TABLE images (
    image_id INT PRIMARY KEY,
    file_name VARCHAR(255),
    resolution VARCHAR(50),
    keywords VARCHAR(255)
);

CREATE TABLE photographers (
    photographer_id INT PRIMARY KEY,
    name VARCHAR(100),
    email VARCHAR(100)
);

CREATE TABLE categories (
    category_id INT PRIMARY KEY,
    name VARCHAR(50)
);

CREATE TABLE Images (
    image_id INT PRIMARY KEY,
    file_name TEXT NOT NULL,
    resolution TEXT,
    keywords TEXT
);

CREATE TABLE ImageTags (
    tag_id INT PRIMARY KEY,
    tag_name TEXT NOT NULL
);

CREATE TABLE ImageKeywords (
    id INT PRIMARY KEY,
    keyword TEXT NOT NULL,
    image_id INT,
    FOREIGN KEY (image_id) REFERENCES Images(image_id)
);

CREATE TABLE ImageCategories (
    category_id INT PRIMARY KEY,
    category_name TEXT NOT NULL
);
