tables.table_schema:
CREATE TABLE EventDetails (
event_id INT PRIMARY KEY,
event_name VARCHAR(255) NOT NULL,
event_date DATE NOT NULL,
location VARCHAR(255) NOT NULL
);

CREATE TABLE ClientRequirements (
client_id INT PRIMARY KEY,
client_name VARCHAR(255) NOT NULL,
client_email VARCHAR(255) NOT NULL,
client_phone VARCHAR(15) NOT NULL
);

CREATE TABLE PhotographerAssignments (
assignment_id INT PRIMARY KEY,
photographer_id INT NOT NULL,
event_id INT NOT NULL,
assignment_date DATE NOT NULL
);

CREATE TABLE PhotographerDetails (
photographer_id INT PRIMARY KEY,
photographer_name VARCHAR(255) NOT NULL,
photographer_email VARCHAR(255) NOT NULL,
photographer_phone VARCHAR(15) NOT NULL
);

CREATE TABLE clients (
    client_id UUID PRIMARY KEY,
    client_name TEXT NOT NULL,
    client_email TEXT NOT NULL,
    client_phone TEXT
);

CREATE TABLE requirements (
    requirement_id UUID PRIMARY KEY,
    event_id UUID REFERENCES events(event_id),
    requirement_details TEXT NOT NULL
);

CREATE TABLE photographers (
    photographer_id UUID PRIMARY KEY,
    photographer_name TEXT NOT NULL,
    photographer_email TEXT NOT NULL
);

CREATE TABLE assignments (
    assignment_id UUID PRIMARY KEY,
    event_id UUID REFERENCES events(event_id),
    photographer_id UUID REFERENCES photographers(photographer_id)
);
