tables.table_schema:
CREATE TABLE backdrops (
  backdrop_id INT PRIMARY KEY,
  backdrop_name VARCHAR(50) NOT NULL,
  backdrop_type VARCHAR(50) NOT NULL,
  location VARCHAR(100) NOT NULL
);

CREATE TABLE locations (
    location_id INT PRIMARY KEY,
    location_name VARCHAR(255),
    location_type VARCHAR(50)
);

CREATE TABLE backdrops (
	backdrop_id SERIAL PRIMARY KEY,
	backdrop_name VARCHAR(100) NOT NULL,
	location_type VARCHAR(50) NOT NULL
);

CREATE TABLE landscapes (
	landscape_id SERIAL PRIMARY KEY,
	landscape_name VARCHAR(100) NOT NULL,
	location_id INT NOT NULL,
	FOREIGN KEY (location_id) REFERENCES backdrops(backdrop_id)
);

CREATE TABLE cityscapes (
	cityscape_id SERIAL PRIMARY KEY,
	cityscape_name VARCHAR(100) NOT NULL,
	location_id INT NOT NULL,
	FOREIGN KEY (location_id) REFERENCES backdrops(backdrop_id)
);

CREATE TABLE Landscapes (
  landscape_id INT PRIMARY KEY,
  landscape_name VARCHAR(50) NOT NULL,
  location VARCHAR(100) NOT NULL,
  photographer_id INT
);

CREATE TABLE Cityscapes (
  cityscape_id INT PRIMARY KEY,
  cityscape_name VARCHAR(50) NOT NULL,
  location VARCHAR(100) NOT NULL,
  photographer_id INT
);

CREATE TABLE IndoorStudioSetups (
  studio_id INT PRIMARY KEY,
  studio_name VARCHAR(50) NOT NULL,
  location VARCHAR(100) NOT NULL,
  photographer_id INT
);

CREATE TABLE Landscapes (
  id INT PRIMARY KEY,
  location_name VARCHAR(255),
  location_description TEXT
);

CREATE TABLE Cityscapes (
  id INT PRIMARY KEY,
  city_name VARCHAR(255),
  city_population INT,
  city_description TEXT
);

CREATE TABLE Studios (
  id INT PRIMARY KEY,
  studio_name VARCHAR(255),
  studio_location VARCHAR(255),
  studio_capacity INT
);

CREATE TABLE PhotoLocations (
  location_id INT PRIMARY KEY,
  location_name VARCHAR(255),
  location_type VARCHAR(255),
  location_description TEXT
);
