CREATE TABLE manuscripts(
  id INT PRIMARY KEY,
  title TEXT,
  author TEXT,
  year_published INT
);

CREATE TABLE literary_works(
  id INT PRIMARY KEY,
  title TEXT,
  author TEXT,
  genre TEXT
);

CREATE TABLE archive(
  id INT PRIMARY KEY,
  manuscript_id INT,
  literary_work_id INT
);

CREATE TABLE locations(
  id INT PRIMARY KEY,
  name TEXT,
  city TEXT,
  country TEXT
);

CREATE TABLE Manuscripts (
    id INT PRIMARY KEY,
