tables.table_schema:
CREATE TABLE top_films (
    id INT PRIMARY KEY,
    title TEXT,
    year INT,
    gross_amount DECIMAL
);

CREATE TABLE film_studios (
    id INT PRIMARY KEY,
    studio_name TEXT,
    address TEXT
);

CREATE TABLE film_reviews (
    id INT PRIMARY KEY,
    film_id INT,
    review TEXT
);

CREATE TABLE films (
    film_id INT PRIMARY KEY,
    title VARCHAR,
    release_date DATE,
    box_office_amount DECIMAL
);

CREATE TABLE actors (
    actor_id INT PRIMARY KEY,
    name VARCHAR,
    age INT,
    nationality VARCHAR
);

CREATE TABLE film_actors (
    film_id INT,
    actor_id INT,
    PRIMARY KEY (film_id, actor_id)
);

CREATE TABLE rankings (
    rank_id INT PRIMARY KEY,
    film_id INT,
    rank INT,
    year INT
);
