CREATE TABLE film_inventory (
    film_id INT NOT NULL,
    dvd_id INT NOT NULL,
    dvd_quantity INT NOT NULL,
    PRIMARY KEY (film_id, dvd_id)
);

CREATE TABLE films (
    film_id INT PRIMARY KEY,
    film_name VARCHAR(255),
    director VARCHAR(255),
    release_date DATE
);

CREATE TABLE inventory (
    inventory_id INT PRIMARY KEY,
    film_id INT,
    quantity INT,
    FOREIGN KEY (film_id) REFERENCES films(film_id)
);

CREATE TABLE films (
  film_id INT PRIMARY KEY,
  film_title VARCHAR(255) NOT NULL,
  film_director VARCHAR(255) NOT NULL
);

CREATE TABLE dvds (
  dvd_id INT PRIMARY KEY,
  film_id INT,
  dvd_name VARCHAR(255) NOT NULL,
  FOREIGN KEY (film_id) REFERENCES films(film_id)
);

CREATE TABLE inventory (
  inventory_id INT PRIMARY KEY,
  dvd_id INT,
  quantity INT DEFAULT 0,
  FOREIGN KEY (dvd_id) REFERENCES dvds(dvd_id)
);

CREATE TABLE Film (film_id INT, film_name VARCHAR(255), release_date DATE, director VARCHAR(255));
CREATE TABLE DVD (dvd_id INT, film_id INT, quantity INT);
CREATE TABLE Inventory (inventory_id INT, dvd_id INT, quantity_in_stock INT);
CREATE TABLE Trigger_History (trigger_id INT, trigger_name VARCHAR(255), timestamp TIMESTAMP);

CREATE TABLE inventory (inventory_id SERIAL PRIMARY KEY,
  dvd_id INT REFERENCES dvd(dvd_id),
