tables.table_schema:
CREATE TABLE Architects (
    architect_id INT PRIMARY KEY,
    architect_name VARCHAR(255),
    architect_email VARCHAR(255),
    architect_phone VARCHAR(15)
);

CREATE TABLE Tags (
    tag_id INT PRIMARY KEY,
    tag_name VARCHAR(50)
);

CREATE TABLE ProjectTags (
    tag_id INT PRIMARY KEY,
    project_id INT,
    tag_name VARCHAR(50),
    FOREIGN KEY (project_id) REFERENCES ArchitecturalProjects(project_id)
);

CREATE TABLE Architects (
    architect_id INT PRIMARY KEY,
    architect_name VARCHAR(255),
    architect_bio TEXT
);
